// рЃњрЃарЃФрЃћрЃџрЃў if/else рЃЕрЃљрЃгрЃћрЃарЃўрЃЌ
function pow(x, n) {
  if (n === 1) {
    return x;
  } else {
    return x * pow(x, n - 1);
  }
}
// рЃбрЃћрЃарЃюрЃљрЃарЃБрЃџрЃў рЃЮрЃърЃћрЃарЃљрЃбрЃЮрЃарЃўрЃА рЃЕрЃљрЃюрЃљрЃгрЃћрЃарЃўрЃЌ
function powTernary(x, n) {
  return n === 1 ? x : x * powTernary(x, n - 1);
}
// рЃерЃћрЃЊрЃћрЃњрЃў рЃЮрЃарЃўрЃЋрЃћ рЃцрЃБрЃюрЃЦрЃфрЃўрЃўрЃАрЃЌрЃЋрЃўрЃА рЃћрЃарЃЌрЃў рЃЊрЃљ рЃўрЃњрЃўрЃЋрЃћ рЃљрЃарЃўрЃА рЃЏрЃљрЃњрЃарЃљрЃЏ рЃбрЃћрЃарЃюрЃљрЃарЃБрЃџрЃў рЃЮрЃърЃћрЃарЃљрЃбрЃЮрЃарЃўрЃЌ рЃЕрЃљрЃюрЃљрЃгрЃћрЃарЃў рЃБрЃцрЃарЃЮ рЃЏрЃЮрЃЎрЃџрЃћ рЃљрЃарЃўрЃА рЃЊрЃљ рЃљрЃа рЃЏрЃЮрЃўрЃЌрЃ«рЃЮрЃЋрЃА рЃќрЃћрЃЊрЃЏрЃћрЃбрЃў if/else рЃЕрЃљрЃюрЃљрЃгрЃћрЃарЃўрЃА рЃЉрЃџрЃЮрЃЎрЃћрЃЉрЃА
console.log(pow(2, 10)); // 1024
console.log(powTernary(2, 10)); // 1024

