function getRandomFoodsForWeek(array) {
  const result = [];
  for (let i = 0; i < 7; ) {
    const randomIndex = Math.floor(Math.random() * array.length);
    const randomFood = array[randomIndex];
    if (!result.includes(randomFood)) {
      result.push(randomFood);
      i++;
    }
  }
  return result;
}

