badNameValidator(pattern: string): ValidatorFn {
  return (control: AbstractControl): ValidationErrors | null => {
    return control.value.includes(pattern)
      ? { badName: 'This is a bad name!' }
      : null;
  };
}

signupForm = this.fb.group({
