public addItem(title: string) {
  const itemToAdd = {
    title: title,
    done: false,
  };

  const headers = new HttpHeaders({
    'Content-Type': 'application/json',
  });

  this.http
    .post<{ id: number }>(this.url, itemToAdd, {
      headers,
    })
    .pipe(
      tap((response) => {
        const newItem = {
          id: response.id,
          ...itemToAdd,
        };
        this.todos$.next([...this.todos$.value, newItem]);
      })
    )
    .subscribe();
}

